import persontest from './image/persontest.jpg'
import { Result } from "../../backend/database/search"
import { useEffect, useState } from 'react';
import useSocket from './context/socket';
function Personalinfo() {
    let info: Result[] = [];
    const socket = useSocket();
    const [personInfo, setpersonInfo] = useState<Result>();
    useEffect(() => {
        socket.on("PersonalInfo", (msg) => {
        //  console.log(msg)
        info[info.length]=msg;
        console.log(info[0].name)
        });
   
        return function socketCleanup() {
          socket.removeAllListeners("PersonalInfo");
        };
    
      });
    if (info) {
        console.log("in")
        return (
            <div className="grid grid-cols-9 gap-5 gap-y-12">
                {Array.from(info).map(entry => {
                    return <div key={entry.name} className="max-w-48  bg-white shadow-lg grid grid-cols-2">
                        <div className="clo-span-1">
                            <table>
                                <tbody>
                                    <tr><td>SN: </td>
                                        <td>{entry.serialnumber}</td></tr>
                                    <tr><td>Name: </td><td>{entry.name}</td></tr>
                                </tbody>
                            </table>
                        </div>
                        <div className="clo-span-1">
                            <img src={persontest}></img>
                        </div>
                    </div>

                })}

            </div>
        );
    }
    else
    {
        return null
    }
}
export default Personalinfo;
