import persontest from './image/persontest.jpg'
import { Result } from "../../backend/database/search"
import { useEffect, useState } from 'react';
import useSocket from './context/socket';
function Personalinfo() {
    const socket = useSocket();
    const [showinfo, setshowinfo] = useState<Result[]>();
    const a = [{
        id: 1, serialnumber: "C0409W-4C7525BC7020",
        section: "maintanence",
        name: 'ken',
        photo: './image/persontest.jpg',
        job: 'job1'
    }]
    setshowinfo(a);
    // const [photoSrc,setphotoSrc]=useState<string>();
    // useEffect(() => {
    //     socket.on("PersonalInfo", (msg) => {
    //         //  console.log(msg)
    //         setshowinfo(msg)
    //     });
    //     return function socketCleanup() {
    //         socket.removeAllListeners("PersonalInfo");
    //     };

    // });
    if (showinfo) {
        console.log("a")
        return (
            <div className="grid grid-cols-9 gap-5 gap-y-12">
                {Array.from(showinfo).map(entry => {
                    return <div key={entry.name} className="max-w-sm  bg-white shadow-lg grid grid-flow-2">
                        <div className="clo-flow-1">
                            <table className='table-fixed'>
                                <tbody>
                                    <tr><td>SN: </td><td>{entry.serialnumber}</td></tr>
                                    <tr><td>Name: </td><td>{entry.name}</td></tr>
                                    <tr><td>Job: </td><td>{entry.job}</td></tr>
                                    <tr><td>section: </td><td>{entry.section}</td></tr>
                                </tbody>
                            </table>
                        </div>
                        <div className="clo-flow-1">
                            <img className=' w-50px h-50px' src={persontest}></img>
                        </div>
                    </div>

                })}

            </div>
        );
    }
    else {
        return null;
    }


}
export default Personalinfo;
