import { useEffect, useState } from 'react';
import './App.css';
import useSocket from './context/socket';

function App() {
  const socket = useSocket();
  let mac = "abc";
  socket.emit("beginTest")
  const [macAddress, setMacAddress] = useState<string>();
  useEffect(() => {
		socket.on("testSerialNumber", (sn: unknown) => {
			if (sn && typeof sn === "string") {
				const mac = sn.toUpperCase().match(/.{1,2}/g)
				if (!mac) {
					console.error("Received invalid MAC Address");
					return;
				}
				setMacAddress(mac.join(":"));
				setTestStatus("Device Scanned, Verifying device functionality.")
			} else {
				console.error("Received invalid MAC Address");
			}
		});
		socket.on("mqttResults", (result: unknown) => {
			console.log(result);
			console.log("Received MQTT results.")
			if (isMqttResults(result)) {
				setWifiOk(result.wifiOk);
				if (result.fuelOk !== null) {
					setFuelGaugeOk(result.fuelOk);
				}
				if (result.dsrcMac !== null) {
					setDsrcMac(result.dsrcMac);
				}
				if (result.uwbOk !== null) {
					setUwbOk(result.fuelOk);
				}
			} else {
				console.log("Received invalid Results");
			}
		});
		return function socketCleanup() {
			socket.removeAllListeners("testSerialNumber");
		};
	});
  return (
    <div className="grid grid-flow-6 h-screen">
      <div className="bg-black grid grid-cols-3 gap-2">
        <div className="text-white text-sm">wifi</div>
        <div className="text-center ">
          <p className="text-white text-sm">Roobuck</p>
        </div>
        <div className="grid grid-flow-3 text-white text-sm text-right">
          <div className="row-span-2 text-sm">bb</div>
          <div className="mb-0">clock</div>
        </div>
      </div>
      <div className=" row-span-4 items-center text-center">
        {/* <div className="p-6 max-w-sm mx-auto bg-white rounded-xl shadow-lg  items-center space-x-4"> */}
        <div className='flex flex-row'>
          <table>
            <tbody>
              <tr><td>MAC:</td> <td>{mac}</td></tr>
              <tr><td>SN:</td><td></td></tr>
            </tbody>
          </table>
        </div>
        {/* <input type="text"id="good" className="text-xl font-medium text-primary"></input> */}
        {/* </div> */}
      </div>
      <div className="bg-black">bottom</div>
    </div>);
}
export default App;
